$baseUrl = "https://knowledge-hub-mcp.harveytagalicud7.workers.dev"
function Post-SimpleContent { param([string]$Content, [string]$Tags, [string]$Source, [string]$Filename) $requestBody = @{ jsonrpc = "2.0"; id = (Get-Random); method = "tools/call"; params = @{ name = "store_context"; arguments = @{ content = $Content; tags = $Tags -split ","; source = $Source } } } | ConvertTo-Json -Depth 5; try { $response = Invoke-WebRequest -Uri $baseUrl -Method POST -Headers @{"Content-Type"="application/json"} -Body $requestBody; Write-Host "Success: $Filename" -ForegroundColor Green; return $response.Content } catch { Write-Host "Failed: $Filename - $($_.Exception.Message)" -ForegroundColor Red; return $null } }
Write-Host "Starting simple sanctuary upload..." -ForegroundColor Cyan
